import React, { Component } from 'react'
import NewsItems from './NewsItems'

export default class News extends Component {





  constructor() {
    super();
    // console.log("I am inside news constructor");
    this.state = {
      articles: [],
      loading: false,
      page :1

    }
  }

  async componentDidMount(){

    let url_api = `https://newsapi.org/v2/top-headlines?country=in&category=business&apiKey=ab1d039580ff49fb96aca13fc6998a05&page=${this.state.page}`;

    let data = await fetch(url_api);

    let parsedData = await data.json();
    console.log(parsedData.totalResults);
    this.setState({
      articles: parsedData.articles,
      totalResults : parsedData.totalResults

    })
  }

  handleNextPage = async () =>{
    console.log("next");

    if( this.state +1 > Math.ceil(this.state.totalResults/20));
    else 
    {

      let url_api = `https://newsapi.org/v2/top-headlines?country=in&category=business&apiKey=ab1d039580ff49fb96aca13fc6998a05&page=${this.state.page +1}`;

      let data = await fetch(url_api);

      let parsedData = await data.json();
      this.setState({
        page: this.page + 1,
        articles: parsedData.articles,
      })
    }

  }
  handlePrevPage = async () =>{
    console.log("prev");

    let url_api = `https://newsapi.org/v2/top-headlines?country=in&category=business&apiKey=ab1d039580ff49fb96aca13fc6998a05&${this.state.page - 1}`;
    let data = await fetch(url_api);
    let parsedData = await data.json();
    this.setState({
      page: this.page - 1,
      articles: parsedData.articles
    })

  }

  render() {
    return (
      <div className='container my-3'>
        <h1>News Monkey - Top HeadLines</h1>
        <div className="row">
          {this.state.articles.map((element) => {
            return <div className="col-md-4" key={element.url}>
              <NewsItems title={element.title} description={element.description} imageUrl={element.urlToImage} newsUrl ={ element.url}/>
            </div>

          })}
        </div>
        <div className="container d-flex justify-content-between">
        <button disabled = {this.state.page <=1} className="btn btn-dark " onClick={this.handlePrevPage} >&larr; Previous</button>
        <button disabled = {this.state.page > Math.ceil(this.state.totalResults/20)} type="button" className="btn btn-dark " onClick={this.handleNextPage}>Next &rarr;</button>
        </div>

      </div>
    )
  }
}
