import React, { Component } from 'react'
import NewsItems from './NewsItems'

export default class News extends Component {



  constructor() {
    super();
    // console.log("I am inside news constructor");
    this.state = {
      articles: [],
      loading: false

    }
  }

  async componentDidMount(){

    let url_api = "https://newsapi.org/v2/top-headlines?country=us&category=business&apiKey=ab1d039580ff49fb96aca13fc6998a05";

    let data = await fetch(url_api);

    let parsedData = await data.json();
    console.log(parsedData);
    this.setState({
      articles: parsedData
    })
  }

  render() {
    return (
      <div className='container my-3'>
        <h2>News Monkey HeadLines</h2>
        <div className="row">
          {this.state.articles.map((element) => {
            return <div className="col-md-4" key={element.url}>
              <NewsItems title={element.title.slice(0, 45)} description={element.description.slice(0, 88)} imageUrl={element.urlToImage} newsUrl ={ element.url}/>
            </div>

          })}
        </div>

      </div>
    )
  }
}
