import React, { Component } from 'react'
import NewsItems from './NewsItems'

export default class News extends Component {





  constructor() {
    super();
    // console.log("I am inside news constructor");
    this.state = {
      articles: [],
      loading: false,
      pages :1

    }
  }

  async componentDidMount(){

    let url_api = `https://newsapi.org/v2/top-headlines?country=in&category=business&apiKey=ab1d039580ff49fb96aca13fc6998a05&${this.state.pages}`;

    let data = await fetch(url_api);

    let parsedData = await data.json();
    this.setState({
      articles: parsedData.articles
    })
  }

  handleNextPage = () =>{
    console.log("next");

    this.setState({
      pages: this.pages + 1
    })

  }
  handlePrevPage = () =>{
    console.log(this.state.pages);

    this.setState({
      pages: this.pages - 1
    })

  }

  render() {
    return (
      <div className='container my-3'>
        <h1>News Monkey - Top HeadLines</h1>
        <div className="row">
          {this.state.articles.map((element) => {
            return <div className="col-md-4" key={element.url}>
              <NewsItems title={element.title} description={element.description} imageUrl={element.urlToImage} newsUrl ={ element.url}/>
            </div>

          })}
        </div>
        <div className="container d-flex justify-content-between">
        <button disabled = {this.state.pages <=1} className="btn btn-dark " onClick={this.handlePrevPage} >&larr; Previous</button>
        <button type="button" className="btn btn-dark " onClick={this.handleNextPage}>Next &rarr;</button>
        </div>

      </div>
    )
  }
}
