import React, { Component } from 'react'

export default class Newsitems extends Component {

  render() {
    let { title, description, imageUrl, newsUrl, author, date, source } = this.props;
    return (
      <div >
        <div className="card">
        <span className="position-absolute top-0 start-100 translate-middle badge rounded-pill bg-danger" style={{left:'100%', zindex:'1' }}>
                {source}
          </span>
          <img src={imageUrl} className="card-img-top" alt="..." />
          <div className="card-body">
            <h5 className="card-title">{title}....</h5>
            <p className="card-text">{description}...</p>

            <p className="card-text"><small className="text-muted">By {!author ? "unknown" : author} on {new Date(date).toGT}</small></p>

            <a href={newsUrl} target="_blank" rel="noreferrer" className="btn btn-primary">Read More</a>
          </div>
        </div>
      </div>
    )
  }
}
